}


div.intro,
div.clients {
height:100%;
display: grid;
grid-gap: 20px;
grid-template-columns: repeat(auto-fit, minmax(480px, 1fr));
place-items: center;
overflow: hidden;
  @include mq(small) {
    grid-gap: 0px;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  }
}
div.blog,
