  margin-bottom:6em;
  overflow: hidden;
}

.intro__photo,
.client__photo {
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: grid;
  place-items: center;
   img {
     width: 80%;
     animation: fadein 2s;
     @include aspect-ratio(1, 1);
      @include mq(mid) {
        width: 100%;
      }
  }
}

.intro__photo,
