  @include mq(small) {
      margin-bottom:6em;
  }
}

footer {
padding: 75px;
height:50vh;
font-family: $sans;
background-color: var(--clr-green-500);
color: var(--clr-rose-100);
  a {
    color: var(--clr-rose-100);
    transition: $default-transition;
    &::after {
      width: 100%;
      height: 1px;
      bottom: -3px;
      left: 0;
      background-color:var(--clr-green-200);
      transform-origin: bottom left;
    }
  }
  .socials,
  .date {
    padding-bottom:$line-height-base;
  }
  .socials a {
    padding:15px;  
    .fab{
      transition: $default-transition;
      &:hover {
        transform: scale(1.25);
      }
    }
  }
  .copyright a,
  .credits {
    color: var(--clr-green-200);
  }
  .copyright a,
  .back-to-top,
  .email {
    @include hover-line-revers;
  }
  .back-to-top {
    position:relative;
    &::after {
    content:url(../img/vector-3.svg);
    top:-15px;
    right: 65px;
    border-bottom:0;
    position: absolute;
    }
  }
  @include mq(mid) {
    padding:25px;
  }

}

div.blog,
footer{
  @include flexCenter(column);
  justify-content: space-around;
  text-align: center;
}


/* 48em = 768px */

@media (min-width: 48em) {
  .header li {
    float: left;
  }
  .header li a {
    margin: 20px 30px;
  }
  .header .menu-nav {
    clear: none;
    float: right;
    max-height: none;
  }
  .header .menu-icon {
    display: none;
  }
}

#consent-popup {
  position:fixed;
  text-align: center;
  top:0;
  left:0;
  right:0;
  padding: 1rem;
  opacity:1;
  transition: opacity 2s ease;
  background-color:var(--clr-umber-300);
  color: var(--clr-rose-100);
  font-family: $sans;
  z-index: 2;
    a {
      color: var(--clr-rose-100);
      border-bottom: 1px solid var(--clr-rose-100);
    }
    p {
      font-size: 16px;
    }
    .close {
      position: relative;
      right: 0px;
      top: 4px;
      width: 32px;
      height: 32px;
      opacity: 0.8;
      border-bottom: none;
    }
    .close:hover {
      opacity: 1;
    }
    .close:before, .close:after {
      position: absolute;
      left: 15px;
      content: ' ';
      height: 16px;
      width: 1px;
      background-color:var(--clr-rose-100);
    }
    .close:before {
      transform: rotate(45deg);
    }
    .close:after {
      transform: rotate(-45deg);
    }    
    &.hidden {
      opacity: 0;
      transition: all 700ms ease 0s;
      transform: translate3d(0px, -156px, 0px);
    }  
}
